import React from 'react';
import { Menu } from 'antd';
import { Link } from 'react-router-dom';

const { SubMenu } = Menu;

export default function MenuReponsive() {

    const selectedLang = getLocale();
    const changeLang = ({ key }) => setLocale(key);

    const locales = ['vi-VN', 'en-US'];
    const languageLabels = {
        'vi-VN': 'Viet Nam',
        'en-US': 'English'
    };
    const languageIcons = {
        'vi-VN': 'ðŸ‡¨ðŸ‡³',
        'en-US': 'ðŸ‡ºðŸ‡¸'
    };

    return (
        <Menu mode="inline" style={{ borderRight: 'none' }}>
            <Menu.Item key={0}>
                <Link to="/about">Account</Link>
            </Menu.Item>
            <Menu.Item key={1}>
                <Link to="/about">Setting</Link>
            </Menu.Item>

            <SubMenu
                key="sub1"
                title={<span>Change Language</span>}
                selectedKeys={[selectedLang]}
                onClick={changeLang}
            >
                {locales.map(locale => (
                    <Menu.Item key={locale}>
                        <span role="img" aria-label={languageLabels[locale]}>
                            {languageIcons[locale]}
                        </span>{' '}
                        {languageLabels[locale]}
                    </Menu.Item>
                ))}
            </SubMenu>

            <Menu.Divider />
            <Menu.Item key={2}>
                <Link to="/about">Logout</Link>
            </Menu.Item>
        </Menu>
    );
}
