import React from 'react';
import { Card, Row, Col } from 'antd';
import { GridContent } from '@ant-design/pro-layout';
import './styles.css';

export default function Account(props) {
    return (
        <GridContent>
            <Row gutter={[16, 16]}>
                <Col xl={7} lg={7} md={7} sm={24} xs={24}>
                    <Card
                        bordered={false}
                        style={{
                            marginBottom: 24
                        }}
                        //loading={dataLoading}
                    >
                        <div className="avatarHolder">
                            <img
                                alt=""
                                src="https://gw.alipayobjects.com/zos/rmsportal/pBjWzVAHnOOtAUvZmZfy.svg"
                            />
                            <div className="name">ao nguyen</div>
                            <div>nguyen duc ao</div>
                        </div>
                        <div className="detail">
                            <p>
                                <i className="title" />
                                title
                            </p>
                            <p>
                                <i className="group" />
                                group
                            </p>
                            <p>
                                <i className="address" />
                                phu hung binh tan tay son binh dinh
                            </p>
                        </div>
                        <Divider dashed />
                        <div className="tags">
                            <div className="tagsTitle">标签</div>
                            <Tag>tag1</Tag>
                            {inputVisible && (
                                <Input
                                    ref={ref => this.saveInputRef(ref)}
                                    type="text"
                                    size="small"
                                    style={{
                                        width: 78
                                    }}
                                    value={inputValue}
                                    onChange={this.handleInputChange}
                                    onBlur={this.handleInputConfirm}
                                    onPressEnter={this.handleInputConfirm}
                                />
                            )}
                            {!inputVisible && (
                                <Tag
                                    onClick={this.showInput}
                                    style={{
                                        background: '#fff',
                                        borderStyle: 'dashed'
                                    }}
                                >
                                    <PlusOutlined />
                                </Tag>
                            )}
                        </div>
                        <Divider
                            style={{
                                marginTop: 16
                            }}
                            dashed
                        />
                        <div className="team">
                            <div className="teamTitle">团队</div>
                            <Row gutter={36}>
                                <Col lg={24} xl={12}>
                                    <Link to={item.href}>
                                        <Avatar size="small" src='https://avatars1.githubusercontent.com/u/8186664?s=460&v=4' />
                                        ant design
                                    </Link>
                                </Col>
                            </Row>
                        </div>
                    </Card>
                </Col>
                <Col xl={17} lg={17} md={17} sm={24} xs={24}>
                    <Card>account</Card>
                </Col>
            </Row>
        </GridContent>
    );
}
