import { GET_PROVINCES, GET_DISTRICT } from './../constants/types';

const initialState = {
    provinces: [],
    district: []
}

export default function (state = initialState, action) {
    switch(action.type)
    {
        case GET_PROVINCES:
            return {
                ...state,
                provinces: action.payload
            }
        case GET_DISTRICT: 
            return {
                ...state,
                district: action.payload
            }
        default:
            return state;
    }
}
