import React from 'react';
import {
    LogoutOutlined,
    SettingOutlined,
    UserOutlined
} from '@ant-design/icons';
import { Avatar, Menu, Spin } from 'antd';
import { FormattedMessage } from 'umi-plugin-react/locale';
import { HeaderDropdown } from '../..';
import './styles.css';

export default function AvatarDropdown(props) {
    const { userCurrent } = props;

    const menuHeaderDropdown = (
        <Menu style={{minWidth: '160px'}} selectedKeys={[]}>
            <Menu.Item key="center">
                <UserOutlined />
                <FormattedMessage id='globalHeader.account' defaultMessage='Account' />
            </Menu.Item>
            <Menu.Item key="settings">
                <SettingOutlined />
                <FormattedMessage id='globalHeader.setting' defaultMessage='Setting' />
            </Menu.Item>
            <Menu.Divider />
            <Menu.Item key="logout">
                <LogoutOutlined />
                <FormattedMessage id='globalHeader.logout' defaultMessage='Logout' />
            </Menu.Item>
        </Menu>
    );

    return userCurrent && userCurrent.name ? (
        
        <HeaderDropdown overlay={menuHeaderDropdown} placement='bottomRight'>
            <span className='dropDown-avatar'>
                <Avatar
                    style={{marginRight: '8px', backgroundColor: '#87d068'}}
                    className='avatar'
                    size="small"
                    icon="user"
                    alt="avatar"
                />
                <span>aonguen</span>
            </span>
        </HeaderDropdown>
    ) : (
        <Spin
            size="small"
            style={{
                margin: '25px 0 0 20px',
                textAlign: 'center'
            }}
        />
    );
}
