import React from 'react';
import {
    PageHeader,
    Menu,
    Dropdown,
    Icon,
    Button,
    Tag,
    Typography,
    Row,
    Card,
    Form,
    Input
} from 'antd';
const { Paragraph } = Typography;
const { TextArea } = Input;

function Home(props) {
    const IconLink = ({ src, text }) => (
        <a
            style={{
                marginRight: 16,
                display: 'flex',
                alignItems: 'center'
            }}
        >
            <img
                style={{
                    marginRight: 8
                }}
                src={src}
                alt="start"
            />
            {text}
        </a>
    );

    const content = (
        <div className="content">
            <Row>
                <Form>
                    <Form.Item>
                        <TextArea rows={4} onChange={onChange} value={value} />
                    </Form.Item>
                    <Form.Item>
                        <Button
                            htmlType="submit"
                            loading={submitting}
                            onClick={onSubmit}
                            type="primary"
                        >
                            Add Comment
                        </Button>
                    </Form.Item>
                </Form>
            </Row>
            <Row type="flex">
                <IconLink
                    src="https://gw.alipayobjects.com/zos/rmsportal/MjEImQtenlyueSmVEfUD.svg"
                    text="Upload"
                />
            </Row>
        </div>
    );

    const Content = ({ children }) => {
        return (
            <Row className="content" type="flex">
                <div className="main" style={{ flex: 1 }}>
                    {children}
                </div>
            </Row>
        );
    };

    return (
        <PageHeader
            title="Ao Nguyen"
            style={{
                border: '1px solid rgb(235, 237, 240)'
            }}
            tags={<Tag color="blue">Online</Tag>}
            avatar={{
                src:
                    'https://avatars1.githubusercontent.com/u/8186664?s=460&v=4'
            }}
        >
            <Content>{content}</Content>
        </PageHeader>
    );
}
export default Home;
