import React from 'react';
import { Form, Input, Row, Col, Select } from 'antd';
const { Item } = Form;
const { TextArea } = Input;
const { Option } = Select;

function FormSetting(props) {
    const { getFieldDecorator, validateFieldsAndScroll } = props.form;

    const handleSubmit = e => {
        e.preventDefault();
        validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }
        });
    };
    const prefixSelector = getFieldDecorator('prefix', {
        initialValue: '87'
    })(
        <Select style={{ width: 70 }}>
            <Option value="86">+86</Option>
            <Option value="87">+87</Option>
        </Select>
    );

    return (
        <div>
            <Row>
                <Col xl={15} lg={15} md={15} sm={24} xs={24}>
                    <Form onSubmit={handleSubmit}>
                        <Item label="Email">
                            {getFieldDecorator('email', {
                                rules: [
                                    {
                                        required: true,
                                        message: 'Please input your email'
                                    }
                                ]
                            })(<Input placeholder="Please input your email" />)}
                        </Item>
                        <Item label="Nickname">
                            {getFieldDecorator('nickname', {
                                rules: [
                                    {
                                        required: true,
                                        message: 'Please input your nickname'
                                    }
                                ]
                            })(
                                <Input placeholder="Please input your nickname" />
                            )}
                        </Item>
                        <Item label="Personal profile">
                            {getFieldDecorator('personalProfile')(
                                <TextArea rows={3} />
                            )}
                        </Item>
                        <Item label="Phone Number">
                            {getFieldDecorator('phone', {
                                rules: [
                                    {
                                        required: true,
                                        message:
                                            'Please input your phone number!'
                                    }
                                ]
                            })(
                                <Input
                                    addonBefore={prefixSelector}
                                    style={{ width: '100%' }}
                                />
                            )}
                        </Item>
                    </Form>
                </Col>
            </Row>
        </div>
    );
}

const FormSettingViews = Form.create({ name: 'form-settings-account' })(
    FormSetting
);
export default FormSettingViews;
