import React, { useState } from 'react';
import { List, message, Avatar, Spin, Card, Icon } from 'antd';
import axios from 'axios';
import { BASE_URI } from './../../../../constants/base_url';

import InfiniteScroll from 'react-infinite-scroller';
import './styles.css';

export default function ManyComponent() {
    const [tracks, setTracks] = useState([]);
    const [hasMoreItems, setHasMoreItems] = useState(true);
    const [loading, setLoading] = useState(false);

    const loadItems = page => {
        // axios
        //     .get(BASE_URI + '/getAllData', {
        //         params: {
        //             page,
        //             page_size: 20
        //         }
        //     })
        //     .then(res => {
        //         const items = res.data;
        //         setTracks([...tracks, ...items.users]);
        //     })
        //     .catch(err => {
        //         setHasMoreItems(false);
        //     });
        const data = [
            {
                id: '1',
                name: 'ao nguyen',
                email: 'aonguyen@gmail.com'
            }
        ];
        setLoading(true);
        if (tracks.length === 50) {
            message.warning('Infinite List loaded all');
            setHasMoreItems(false);
            setLoading(false);
            return;
        }
        setTimeout(
            setTracks([...tracks, ...data])
        , 5000);
    };

    return (
        <>
            <h3>danh sach cac ten user</h3>
            <InfiniteScroll
                pageStart={0}
                //initialLoad={false}
                loadMore={loadItems}
                hasMore={hasMoreItems}
                //useWindow={false}
                loader={
                    <Spin
                        indicator={
                            <Icon type="loading" style={{ fontSize: 20 }} />
                        }
                        key={0}
                        tip="loading..."
                    />
                }
            >
                <div className="tracks">
                    <ol>
                        {tracks.map((value, key) => (
                            <li key={key}>{value.name}</li>
                        ))}
                    </ol>
                </div>
            </InfiniteScroll>
        </>
    );
}

