import React, { useState } from 'react';
import { GridContent } from '@ant-design/pro-layout';
import { Menu } from 'antd';
import './styles.css';

const { Item } = Menu;
const menuMap = {
    base: 'base',
    security: 'sejhajnd',
    binding: 'abihans',
    notification: 'sasas'
};

export default function Setting() {
    const [selectKey, setSelectKey] = useState('base');
    const [mode, setMode] = useState('inline');

    const getMenu = () => {
        return Object.keys(menuMap).map(item => (
            <Item key={item}>{menuMap[item]}</Item>
        ));
    };
    const getRightTitle = () => {
        return menuMap[selectKey];
    };
    const renderChildren = () => {
        switch (selectKey) {
            case 'base':
                return <h1>base</h1>;

            case 'security':
                return <h1>security</h1>;

            case 'binding':
                return <h1>binding</h1>;

            case 'notification':
                return <h1>notification</h1>;

            default:
                break;
        }
        return null;
    };

    return (
        <GridContent>
            <div
                className="main"
                ref={ref => {
                    if (ref) {
                        this.main = ref;
                    }
                }}
            >
                <div className="leftMenu">
                    <Menu
                        mode={mode}
                        selectedKeys={[selectKey]}
                        onClick={({ key }) => selectKey(key)}
                    >
                        {getMenu()}
                    </Menu>
                </div>
                <div className="right">
                    <div className="title">{getRightTitle()}</div>
                    {renderChildren()}
                </div>
            </div>
        </GridContent>
    );
}
