import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Row, Col } from 'antd';

//import { GlobalLoading } from './../../components';
import { ToolPost, ListContent, ToolWeather } from './Components';
import allActions from './../../actions';
import './styles.css';

function Home(props) {
    const { history } = props;
    const geocode = useSelector(state => state.currentPlace.geocode);
    const dispatch = useDispatch();

    useEffect(() => {
        if(navigator.geolocation)
        {
            function success(position) {
                const lat = position.coords.latitude;
                const lon = position.coords.longitude;
                dispatch(allActions.geocodeActions.getCurrentPlace(lat, lon, history));
            }
            function error(err) {
                console.log(err);
            }
            navigator.geolocation.getCurrentPosition(success, error)
        }
    }, [dispatch, history])
    
    return (
        <>
            <Row gutter={[20, 20]}>
                <Col lg={10} md={10}>
                    <ToolPost />
                </Col>
                <Col lg={14} md={14} sm={24}>
                    <ToolWeather geocode={geocode} />
                </Col>
            </Row>
            <Row gutter={[20, 20]}>
                <Col>
                    <ListContent />
                </Col>
            </Row>
        </>
    );
}
export default Home;
