import React from 'react';
import { Layout, Menu, Icon } from 'antd';
import { FormattedMessage } from 'umi-plugin-react/locale';
import { siderMenu } from './../../constants/sider-menu';
import './styles.css';

const { Sider } = Layout;

export default function SiderMenu() {
    console.log(siderMenu)

    const handleBreak = broken => {};
    const handleCollapse = (collapsed, type) => {};

    return (
        <Sider
            className='sider-menu'
            theme="light"
            breakpoint="lg"
            collapsedWidth="0"
            onBreakpoint={handleBreak}
            onCollapse={handleCollapse}
        >
            <Menu
                theme="light"
                mode="inline"
                defaultSelectedKeys={['4']}
                //style={{ borderRight: 'none' }}
            >
                <Menu.Item key="1">
                    <Icon type="user" />
                    <FormattedMessage id="menu.friend" />
                </Menu.Item>
                <Menu.Item key="2">
                    <Icon type="history" />
                    <FormattedMessage id="menu.history" />
                </Menu.Item>
                <Menu.Item key="3">
                    <Icon type="message" />
                    <FormattedMessage id="menu.message" />
                </Menu.Item>
            </Menu>
        </Sider>
    );
}
