import React from 'react';
import {
    PageHeader,
    Menu,
    Dropdown,
    Icon,
    Button,
    Tag,
    Typography,
    Row,
    Card,
    Form,
    Input
} from 'antd';
const { TextArea } = Input;

function Home(props) {

    const content = (
        <div className="content">
            <Row>
                <Form>
                    <Form.Item>
                        <TextArea rows={4} placeholder='Ban dang nghi gi' />
                    </Form.Item>
                </Form>
            </Row>
        </div>
    );

    const Content = ({ children }) => {
        return (
            <Row className="content" type="flex">
                <div className="main" style={{ flex: 1 }}>
                    {children}
                </div>
            </Row>
        );
    };

    return (
        <Card 
            title='Create Post' 
            size='small'
            actions={[
                <Icon title='post' type="edit" key="edit" />,
                <Icon title='upload' type="upload" key="setting" />,
            ]}
        >
            <PageHeader
                title="Ao Nguyen"
                tags={<Tag color="blue">Online</Tag>}
                avatar={{
                    src:
                        'https://avatars1.githubusercontent.com/u/8186664?s=460&v=4'
                }}
                style={{margin: '0', padding: '0'}}
            >
                <Content>{content}</Content>
            </PageHeader>
        </Card>
    );
}
export default Home;
