import React, { forwardRef, useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Spin, Select, Row, Col } from 'antd';
import allActions from './../../../../actions';
import './styles.css';
const { Option } = Select;
const nullSelectItem = {
    label: '',
    key: ''
};

const GeographicView = forwardRef((props, ref) => {
    const [loadingProvinces, setLoadingProvinces] = useState(false);
    const dispatch = useDispatch();
    const provinces = useSelector(state => state.provincesReducer.provinces);
    let districts = useSelector(state => state.provincesReducer.districts);

    useEffect(() => {
        dispatch(allActions.geocodeActions.getProvinces());
    }, [dispatch]);

    const conversionObject = () => {
        const { value } = props;
        if (!value) {
            setLoadingProvinces(false);
            return {
                province: nullSelectItem,
                city: nullSelectItem
            };
        }
        const { province, city } = value;
        return {
            province: province || nullSelectItem,
            city: city || nullSelectItem
        };
    };

    const getProvinceOption = () => {
        if (provinces.length !== 0) {
            return getOption(provinces);
        }
        return [];
    };
    const getDistrictOption = () => {
        if (districts.length !== 0) {
            return getOption(districts);
        }
        return [];
    };
    const getOption = list => {
        return list.map(item => (
            <Option key={item.id} value={item.id}>
                {item.name}
            </Option>
        ));
    };
    const selectProvinceItem = item => {
        const { onChange } = props;

        setLoadingProvinces(true);
        dispatch(allActions.geocodeActions.getDistricts(item.key));
        if (onChange) {
            onChange({
                province: item,
                city: nullSelectItem
            });
        }
    };
    const selectCityItem = item => {
        const { value, onChange } = props;

        if (value && onChange) {
            onChange({
                province: value.province,
                city: item
            });
        }
    };

    const { province, city } = conversionObject();
    return (
        <Row>
            <Col xl={24} lg={24} md={24} sm={24} xs={24}>
                <Spin
                    spinning={loadingProvinces}
                    wrapperClassName="row"
                    size="small"
                >
                    <Select
                        className="item"
                        showSearch
                        onSelect={selectProvinceItem}
                        loading={provinces.length !== 0 ? false : true}
                        labelInValue
                        value={province}
                    >
                        {getProvinceOption()}
                    </Select>
                    <Select
                        className="item"
                        showSearch
                        labelInValue
                        value={city}
                        onSelect={selectCityItem}
                    >
                        {getDistrictOption()}
                    </Select>
                </Spin>
            </Col>
        </Row>
    );
});

export default GeographicView;
