import React, { useState } from 'react';
import { GridContent } from '@ant-design/pro-layout';
import { Menu } from 'antd';
import { BasicSetting } from './components';
import './styles.css';

const { Item } = Menu;
const menuMap = {
    base: 'Basic Settings',
    security: 'Security Settings',
    notification: 'Message Notification'
};

export default function Setting() {
    const [selectKey, setSelectKey] = useState('base');

    const getMenu = () => {
        return Object.keys(menuMap).map(item => (
            <Item key={item}>{menuMap[item]}</Item>
        ));
    };
    const getRightTitle = () => {
        return menuMap[selectKey];
    };
    const renderChildren = () => {
        switch (selectKey) {
            case 'base':
                return <BasicSetting />;

            case 'security':
                return <h1>security</h1>;

            case 'notification':
                return <h1>notification</h1>;

            default:
                break;
        }
        return null;
    };
    const selectedKey = key => {
        setSelectKey(key);
    };

    return (
        <GridContent>
            <div className="main">
                <div className="leftMenu-account">
                    <Menu
                        mode='inline'
                        selectedKeys={[selectKey]}
                        onClick={({ key }) => selectedKey(key)}
                    >
                        {getMenu()}
                    </Menu>
                </div>
                <div className="right-account">
                    <div className="title">{getRightTitle()}</div>
                    {renderChildren()}
                </div>
            </div>
        </GridContent>
    );
}
