import React from 'react';
import { Menu } from 'antd';
import { getLocale, setLocale, FormattedMessage } from 'umi-plugin-react/locale';
import { Link } from 'react-router-dom';

const { SubMenu } = Menu;

export default function MenuReponsive() {

    const selectedLang = getLocale();
    const changeLang = ({ key }) => setLocale(key);

    const locales = ['vi-VN', 'en-US'];
    const languageLabels = {
        'vi-VN': 'Viet Nam',
        'en-US': 'English'
    };
    const languageIcons = {
        'vi-VN': 'ðŸ‡¨ðŸ‡³',
        'en-US': 'ðŸ‡ºðŸ‡¸'
    };

    return (
        <Menu mode="inline" style={{ borderRight: 'none' }}>
            <Menu.Item key={0}>
                <Link to="/about">
                    <FormattedMessage id='globalHeader.account' defaultMessage='Account' />
                </Link>
            </Menu.Item>
            <Menu.Item key={1}>
                <Link to="/about">
                    <FormattedMessage id='globalHeader.setting' defaultMessage='Setting' />
                </Link>
            </Menu.Item>

            <SubMenu
                key="sub1"
                title={<span><FormattedMessage id='globalHeader.lang' defaultMessage='Language' /></span>}
                onClick={changeLang}
            >
                {locales.map(locale => (
                    <Menu.Item key={locale}>
                        <span role="img" aria-label={languageLabels[locale]}>
                            {languageIcons[locale]}
                        </span>{' '}
                        {languageLabels[locale]}
                    </Menu.Item>
                ))}
            </SubMenu>

            <Menu.Divider />
            <Menu.Item key={2}>
                <Link to="/about">
                    <FormattedMessage id='globalHeader.logout' defaultMessage='Logout' />
                </Link>
            </Menu.Item>
        </Menu>
    );
}
